import { createStore } from 'vuex';

export default createStore({
    state:{
         listaRespuestas: [],
    },
    mutations:{
        ['AGREGAR_RESPUESTA'] (state, respuesta){
            state.listaRespuestas.push('Nuevo Estado');
        }

    },
    actions:{
        agregarRespuesta:({commit}) => {
            commit('AGREGAR_RESPUESTA');
        }
    }
});
